
public class LoopssDoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = -1;
		
		/*
		 * while(a>=0){
			
			System.out.println("The value of a is " + a);
			a--;
		}
		 */
		
		do {
			System.out.println("The value of a is " + a);
			a--;
		} while(a>=0);
		
		System.out.println("This is out of loop");
		


	}

}
