
public class LoopssFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Print 200 to 0 using for loop
		
		for(int a=200;a>=0;a--)
		{
			System.out.println("The value of a is " + a);
		}
		
		// print 1 to 100 using for loop
		
		for(int a=1;a<=100;a++)
		{
			System.out.println("The value of a is " + a);
		}
	}

}
