
public class SwitchMonth {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			
			int a = 3;
			
			
			switch(a){
			case 0:
				System.out.println("This is January");
				break;
			case 1:
				System.out.println("This is February");
				break;
			case 2:
				System.out.println("This is March");
				break;
			case 3:
				System.out.println("This is April");
				break;
			case 4:
				System.out.println("This is May");
				break;
			case 5:
				System.out.println("This is June");
				break;
			case 6:
				System.out.println("This is July");
				break;
			case 7:
				System.out.println("This is August");
				break;
			case 8:
				System.out.println("This is September");
				break;
			case 9:
				System.out.println("This is October");
				break;
			case 10:
				System.out.println("This is November");
				break;
			case 11:
				System.out.println("This is December");
				break;
				
			default:
					System.out.println("The number provided is out of range");
									
			}
			
			System.out.println("This is out of Switch");
		}


	}


